import React, { useRef } from 'react'
import Card from 'react-bootstrap/Card'

function VideoTranscript (props) {
  const cardRef = useRef(null)
  const showScrollBar = cardRef.current
    ? cardRef.current.scrollHeight > cardRef.current.clientHeight
    : false

  return (
    <div>
      <Card
        body
        className='shadow-none p-3 mb-3 bg-dark'
        style={{
          width: '500px',
          height: '120px',
          overflowY: showScrollBar ? 'scroll' : 'hidden',
          borderRadius: '10px',
          color: 'white'
        }}
        ref={cardRef}
      >
        {'Video Transcript: ' + props.text}
      </Card>
    </div>
  )
}

export default VideoTranscript
