import React from 'react'
import { useEffect, useState } from 'react'
import axios from 'axios'
import VideoPlayer from './VideoPlayer'
import CardOverlay from './CardOverlay'
import FileUpload from './FileUpload'

function VideoPlayerCard (props) {
  const [displayConditional, setDisplayConditional] = useState(true)
  const [videoConditional, setVideoConditional] = useState(true)
  const [selectedFile, setSelectedFile] = useState('')
  const [selectedFileUrl, setSelectedFileUrl] = useState('')
  const [loadVideo, setLoadVideo] = useState(props.autoplayControl)

  const ans = props.text !== '' ? true : false

  const [handleLoad, setHandleLoad] = useState(ans)

  const pull_data = data => {
    console.log('Video Player Card: ' + data)
    props.func(data)
  }

  const pull_MOV_path = data => {
    console.log('MOV path: ' + data)
    props.func2(data)
  }

  useEffect(() => {
    console.log('Autoplay in card loaded: ' + props.autoplayControl)
    setLoadVideo(props.autoplayControl)
    console.log('Changed to the following')
  }, [props.autoplayControl, props.text])

  return displayConditional ? (
    <CardOverlay
      render={
        <FileUpload
          func={pull_data}
          func2={pull_MOV_path}
          loadVideo={handleLoad}
          width={400}
          height={300}
          style={{ borderTopLeftRadius: '26px' }}
        />
      }
    />
  ) : (
    <VideoPlayer outputFile={props.outputFile} autoplayControl={true} />
  )
}

export default VideoPlayerCard
