import React, { useState, useEffect } from 'react'
import VideoPlayerCard from './components/VideoPlayerCard'
import Header from './components/Header'
import DataComponent from './components/DataComponent'
import VideoTranscript from './components/VideoTranscript'

const App = () => {
  const [outputCsv, setOutputcsv] = useState(null)
  const [dataLoaded, setDataLoaded] = useState(false)

  const pull_data = data => {
    console.log('App.js: ' + data)
    setOutputcsv(data)
    console.log('CSV in parent component: ' + outputCsv)
    setDataLoaded(true)
    console.log('Data loaded: ' + dataLoaded)
  }

  useEffect(() => {
    console.log('CSV in parent component: ' + outputCsv)
  }, [outputCsv])

  useEffect(() => {
    console.log('Data loaded: ' + dataLoaded)
  }, [dataLoaded])

  const dataStyles = {
    videoOverlay: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      maxWidth: '1350px',
      margin: '0 auto'
    }
  }

  return (
    <>
      <div className='App'>
        <Header logoName='Sentivision' />
        <br />
        <>
          <div className='videoOverlay' style={dataStyles.videoOverlay}>
            <div>
              <VideoPlayerCard
                func={pull_data}
                handleSubmission={setOutputcsv}
              />
              <VideoTranscript text='Video Transcript' />
            </div>
            <div>
              <DataComponent outputFile={outputCsv} condition={dataLoaded} />
            </div>
          </div>
        </>
        <br />
      </div>
    </>
  )
}

export default App
